import { Component, OnInit } from '@angular/core';

declare const google: any;

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent implements OnInit {
  ngOnInit() {
    this.initMap();
  }

  async initMap(): Promise<void> {
    const startLoc = { lat: -43.528, lng: 172.601 };
    const map = new google.maps.Map(document.getElementById("map"), {         
        center: startLoc,
        zoom: 8,
      }
    );
    new CLickEventHandler(map, startLoc);
  }

  class CLickEventHandler {
    origin: google.maps.LatLngLiteral;
    map: google.maps.Map;
    directionsService: google.maps.DirectionsService;
    directionsRenderer: google.maps.DirectionsRenderer;
    placesService: google.maps.places.PlacesService;
    infowindow: google.maps.InfoWindow;
    infowindowContent: HTMLElement;
    constructor(map: google.maps.Map, origin: google.maps.LatLngLiteral) {
      this.origin = origin;
      this.map = map;
      this.directionsService = new google.maps.DirectionsService();
      this.directionsRenderer = new google.maps.DirectionsRenderer();
      this.directionsRenderer.setMap(map);
      this.placesService = new google.maps.places.PlacesService(map);
      this.infowindow = new google.maps.InfoWindow();
      this.infowindowContent = document.getElementById(
        "infowindow-content"
      ) as HTMLElement;
      this.infowindow.setContent(this.infowindowContent);

      // Listen for clicks on the map.
      this.map.addListener("click", this.handleClick.bind(this));
    }

  onPinLocation(event,startLoc){
    console.log(event);
  }
}
